    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed
        // TODO add your handling code here:
        List<DBConfigManagementBeans> ldbcm = new ArrayList<>();
        Integer qtdetermianis = jTTerminais.getRowCount() + 1;
        for (int i = 0; i < jTTerminais.getRowCount(); i++) {
            DBConfigManagementBeans dbcm = new DBConfigManagementBeans();
            dbcm.setTerminal((Integer) jTTerminais.getValueAt(i, 0));
            dbcm.setDirdb(jTTerminais.getValueAt(i, 1).toString());
            dbcm.setUser(jTTerminais.getValueAt(i, 2).toString());
            dbcm.setPassword(jTTerminais.getValueAt(i, 3).toString());
            dbcm.setQtdeterminal(qtdetermianis);
            ldbcm.add(dbcm);
        }
        DBConfigManagementBeans dbcm2 = new DBConfigManagementBeans();
        dbcm2.setDirdb(jTlocalbanco1.getText());
        dbcm2.setPassword(jTsenhabanco1.getText());
        dbcm2.setUser(jTusuariobanco1.getText());
        dbcm2.setTerminal((Integer) jSTerminal1.getValue());
        dbcm2.setQtdeterminal(qtdetermianis);
        ldbcm.add(dbcm2);
        try {
            new DBConfigManagement().createConfigManagement(ldbcm);
            getTerminais();
            jDLocalTerminal.setVisible(false);
        } catch (IOException ex) {
            JOptionPane.showMessageDialog(this, "Erro ao Salvar" + ex);
        }
    }//GEN-LAST:event_jButton7ActionPerformed

